angular.module('app', ['control']);

var control = angular.module('control', ["ngTouch", "ngRoute"]);

control.controller('generatorOptions', function ($http, $scope, $filter, $routeParams) {

    $http.get('buttons.json')
        .then(function(res){
            $scope.buttonStyles = res.data;
    });

    if($routeParams.buttonId == undefined) {
        $scope.buttonId = 1;
    } else {
        $scope.buttonId = $routeParams.buttonId;
    }

});

control.config(function($routeProvider) {
    $routeProvider

        // route for the home page
        .when('/', {
            templateUrl : 'views/container.html'
        })

        // route for the about page
        .when('/:buttonId', {
            templateUrl : 'views/container.html'
        })

        .otherwise({
            templateUrl : 'views/404.html'
        });
});

//http://jsfiddle.net/MqM76/217/
control.directive('customAutocomplete', function () {
    return {
        link : function (scope, element, attrs) {
            var $element = angular.element(element),
                availableTutorials = [
                    "width",
                    "padding",
                    "margin",
                    "box-shadow",
                    "text-shadow"
                ];


            $element.autocomplete({
                source: availableTutorials,
                autoFocus:true,
                delay: 0
            });
        }
    };
});

control.directive('ngEnter', function () {
    return function (scope, element, attrs) {
        element.bind("keydown keypress", function (event) {
            if(event.which === 13) {
                scope.$apply(function (){
                    scope.$eval(attrs.ngEnter);
                });

                event.preventDefault();
            }
        });
    };
});

control.directive('inputWidth', function () {
    return function (scope, element, attrs) {
        $.fn.textWidth = function(text, font) {
            if (!$.fn.textWidth.fakeEl) $.fn.textWidth.fakeEl =      $('<span>').hide().appendTo(document.body);
            $.fn.textWidth.fakeEl.text(text || this.val() || this.text()).css('font', font || this.css('font'));
            return $.fn.textWidth.fakeEl.width();
        };

        $('#hiddenInput').on('input', function() {
            var padding = 10; //Works as a minimum width
            var valWidth = ($(this).textWidth() + padding) + 'px';
            $('#'+this.id+'-width').html(valWidth);
            $('#hiddenInput').css('width', valWidth);
        }).trigger('input');
    };
});
